//import Profile from "../../../../assets/images/profile.png";
import Profile from "../../assets/images/profile.png";
import Man from "../../assets/images/man.png";

import "./style.css";

const SideBar: React.FC = () => {

    const chatList = [
        { userPhoto: Man, userName: "Sajith", lastMessage: "hello" },
        { userPhoto: Profile, userName: "Pradeep", lastMessage: "hi" }
    ]

    return (
        <div className="Sidebar">
            <div className="SidebarHeader">
                <h1>WhatsApp</h1>
            </div>
            <div className="SidebarChats">
                {/* Display a list of chats here */}
                {chatList.map(chat =>
                    <div className="Chat">
                        <img src={Man} alt="User Avatar" />
                        <div className="ChatInfo">
                            <h2>{chat.userName}</h2>
                            <p>{chat.lastMessage}</p>
                        </div>
                    </div>
                )}
                <div className="Chat">
                    <img src={Man} alt="User Avatar" />
                    <div className="ChatInfo">
                        <h2>Contact Name</h2>
                        <p>Last Message</p>
                    </div>
                </div>
                <div className="Chat">
                    <img src={Profile} alt="User Avatar" />
                    <div className="ChatInfo">
                        <h2>Contact Name</h2>
                        <p>Last Message</p>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default SideBar;
