import axios from "axios";
//import * as constants from "../../../lib/constants";
//import { HTTP_CODE } from "../../../lib/enums/httpData";
//import { ERROR_POPUP_MESSAGE } from "../../../lib/properties/properties";

const networkClient = (headers) => {

    const axiosInstance = axios.create({
        baseURL: process.env.REACT_APP_BASE_URL
    });

    axiosInstance.interceptors.request.use(
        config => {
            if (headers) {
                if (headers.token) {
                    //config.headers[constants.HEADER_KEY_AUTHORIZATION] = headers.token;
                }
            }
            //config.headers[constants.HEADER_KEY_API_KEY] = process.env.REACT_APP_API_KEY;
            //config.headers[constants.HEADER_KEY_ACCEPT] = constants.HEADER_VAL_ACCEPT;
            //config.headers[constants.HEADER_KEY_PLATFORM] = constants.HEADER_VAL_PLATFORM;
            return config;
        }, error => {
            return Promise.reject(error);
        }
    );

    axiosInstance.interceptors.response.use(
        response => {
            return response;
        }, error => {
            //let icMessage = error.response.headers["ic-message"];
            //if (error.response.status !== HTTP_CODE.BAD_REQUEST) {
            //    icMessage = ERROR_POPUP_MESSAGE;
            //}
            const icError = {
                statusCode: error.response.status,
                errorMessage: "",
                externalErrorMessage: ""
            };
            return Promise.reject(icError);
        }
    );

	return axiosInstance;

};

export default networkClient;
