import Man from "../../../assets/images/man.png";
import { SEND_BTN_LBL } from "../../../lib/properties/properties";

import "./style.css";

interface ChatWindowProps {
    onClickChat: (chat: any) => void,
    selectedChat
    //chatList: User[] // here should be a customized type 
}


const ChatWindow: React.FC = ({selectedChat}) => {
    return (
        <div className="ChatWindow">
            {/* Header with contact info */}
            <div className="ChatHeader">
                <img src={Man} alt="User Avatar" />
                <div className="ChatInfo">
                    <h2>Contact Name</h2>
                    <p>Last Seen: 5 minutes ago</p>
                </div>
            </div>

            {/* Messages */}
            <div className="ChatMessages">
                {/* Display messages here */}
                <div className="Message">
                    <p>Message content</p>
                </div>
            </div>

            {/* Message input */}
            <div className="ChatInput">
                <input type="text" placeholder="Type a message" />
                <button>{SEND_BTN_LBL}</button>
            </div>
        </div>
    )
}

export default ChatWindow;
